from concurrent.futures import ThreadPoolExecutor
import fire
import os

N_PROCESSES = 50
N_ITERS = 10

def do_write(uri: str):
    tab =  pa.table({
        'i': pa.array(range(10)),
    })
    return lance.write_dataset(tab, uri, mode="append")

def write_in_loop(uri: str):
